# -*- coding: utf-8 -*-# @Author  : leiziimport requests,jsonfrom Public.log import LOG,logger@logger('requests封装')class requ():    def get(self, url,par,headers):#get消息        try:            r = requests.get(url, headers=headers,params=par)            r.encoding = 'UTF-8'            json_response = json.loads(r.text)            return json_response        except Exception as e:            LOG.info('get请求出错，出错原因:%s'%e)            print('get请求出错,出错原因:%s'%e)            return {}    def post(self, url, params,headers):#post消息        data = json.dumps(params)        try:            r =requests.post(url,params=params,headers=self.headers)            json_response = json.loads(r.text)            return json_response        except Exception as e:            LOG.info('post请求出错，出错原因:%s' % e)            print('post请求出错,原因:%s'%e)    def delfile(self,url,params,headers):#删除的请求        try:            del_word=requests.delete(url,params,headers=self.headers)            json_response=json.loads(del_word.text)            return json_response        except Exception as e:            LOG.info('del请求出错，出错原因:%s' % e)            print('del请求出错,原因:%s' % e)            return {}    def putfile(self,url,params,headers):#put请求        try:            data=json.dumps(params)            me=requests.put(url,data)            json_response=json.loads(me.text)            return json_response        except Exception as e:            LOG.info('put请求出错，出错原因:%s' % e)            print('put请求出错,原因:%s'%e)            return json_response